components/under-construction.tsx.content:
"use client"

import { Construction, ArrowLeft, Hammer } from 'lucide-react'
import { Button } from '@/components/ui/button'
import Link from 'next/link'
import { motion } from 'framer-motion'

interface UnderConstructionProps {
  title?: string
  description?: string
  icon?: React.ReactNode
}

export function UnderConstruction({ 
  title = "Em Constru√ß√£o",
  description = "Esta p√°gina est√° sendo desenvolvida. Em breve estar√° dispon√≠vel com novos recursos incr√≠veis.",
  icon
}: UnderConstructionProps) {
  return (
    <div className="min-h-screen bg-gradient-to-b from-[#1C2526] via-[#1C2526] to-black flex items-center justify-center px-4">
      <div className="max-w-2xl w-full text-center">
        {/* Animated Icon */}
        <motion.div
          initial={{ scale: 0, rotate: -180 }}
          animate={{ scale: 1, rotate: 0 }}
          transition={{ 
            type: "spring",
            stiffness: 260,
            damping: 20,
            duration: 0.8 
          }}
          className="mb-8 flex justify-center"
        >
          <div className="relative">
            {/* Pulsing Circle */}
            <motion.div
              animate={{ 
                scale: [1, 1.2, 1],
                opacity: [0.5, 0.2, 0.5]
              }}
              transition={{ 
                duration: 2,
                repeat: Infinity,
                ease: "easeInOut"
              }}
              className="absolute inset-0 bg-[#34C759] rounded-full blur-xl"
            />

            {/* Icon Container */}
            <div className="relative bg-[#34C759]/20 backdrop-blur-sm border border-[#34C759]/30 rounded-full p-8">
              {icon || <Construction size={64} className="text-[#34C759]" strokeWidth={1.5} />}
            </div>
          </div>
        </motion.div>

        {/* Title */}
        <motion.h1 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 0.6 }}
          className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-6 tracking-tight"
        >
          {title}
        </motion.h1>

        {/* Description */}
        <motion.p 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4, duration: 0.6 }}
          className="text-lg md:text-xl text-white/70 mb-12 leading-relaxed max-w-xl mx-auto"
        >
          {description}
        </motion.p>

        {/* Progress Indicator */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6, duration: 0.6 }}
          className="mb-12"
