    transition: {
      duration: isMobile ? 0.4 : 0.8,
      delay: isMobile ? delay * 0.5 : delay,
      ease: (isMobile ? [0.25, 0.1, 0.25, 1] : [0.22, 1, 0.36, 1]) as [number, number, number, number]
