  }

  button::-webkit-scrollbar,
  .inline-flex::-webkit-scrollbar,
  [class*="inline-flex"]::-webkit-scrollbar {
    display: none !important;
    width: 0 !important;
    height: 0 !important;
  }

  /* Elegant link transitions - Apple style */
  a {
    text-decoration: none;
    -webkit-tap-highlight-color: transparent;
  }

  /* Custom scrollbar styling - Ultra-premium feel */
  ::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }

  ::-webkit-scrollbar-track {
    background: transparent;
  }

  ::-webkit-scrollbar-thumb {
    background: rgba(0, 0, 0, 0.12);
    border-radius: 10px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: rgba(0, 0, 0, 0.2);
  }

  /* Firefox scrollbar */
  * {
    scrollbar-width: thin;
    scrollbar-color: rgba(0, 0, 0, 0.12) transparent;
  }

  /* Ultra-refined text selection - OpenAI style */
  ::selection {
    background: rgba(0, 0, 0, 0.08);
    color: inherit;
  }

  ::-moz-selection {
    background: rgba(0, 0, 0, 0.08);
    color: inherit;
  }

  /* Input styling - Apple iOS style */
  input, textarea {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    font-weight: 300;
  }

  /* Remove default button styling */
  button {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
  }

  /* Image optimization */
  img {
    image-rendering: -webkit-optimize-contrast;
    image-rendering: crisp-edges;
  }
}

/* Prevenir Scroll Horizontal - Fix Global (Refinado) */
@layer base {
  /* Apenas elementos que podem causar overflow horizontal */
  .page-content {
    overflow-x: hidden !important;
    max-width: 100vw !important;
  }

  /* Se√ß√µes de conte√∫do (n√£o navbar ou elementos fixos) */
  section:not([class*="fixed"]):not([class*="absolute"]) {
    overflow-x: hidden !important;
    max-width: 100vw !important;
  }

  /* Containers de conte√∫do espec√≠ficos */
