lib/properties-client.ts.content:
// Client-side functions to fetch properties from Firebase
"use client"

import { useEffect, useState } from 'react'
import { getProperties, getHomepageSettings } from './admin-helpers'
import { Property as AdminProperty, HomepageSettings } from './admin-types'

// Convert admin property to display property format
function convertToDisplayProperty(prop: AdminProperty): any {
  // Create gallery array from images array or single image
  const gallery = prop.images && prop.images.length > 0 
    ? prop.images 
    : prop.image 
      ? [prop.image] 
      : ['/images/placeholder.jpg']

  return {
    id: prop.id || '',
    title: prop.title,
    location: prop.location,
    price: prop.price,
    beds: prop.beds || '0',
    baths: prop.baths || '0',
    sqft: prop.area || '0',
    image: prop.image || gallery[0] || '/images/placeholder.jpg',
    gallery: gallery,
    tag: prop.tag || 'Available',
    type: prop.type || 'Property',
    featured: prop.featured || false,
    status: 'Available', // Default status
    description: prop.longDescription || prop.description || '',
    longDescription: prop.longDescription || prop.description || '',
    features: prop.features || [],
    amenities: prop.amenities || [],
    yearBuilt: prop.yearBuilt || new Date().getFullYear().toString(),
    highlights: [],
  }
}

export function useProperties() {
  const [properties, setProperties] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    async function loadProperties() {
      try {
        setLoading(true)
        const data = await getProperties()
        const converted = data.map(convertToDisplayProperty)
        setProperties(converted)
      } catch (err: any) {
        console.error('Error loading properties:', err)
        setError(err.message || 'Failed to load properties')
        // Fallback to empty array on error
        setProperties([])
      } finally {
        setLoading(false)
      }
    }

    loadProperties()
  }, [])

  return { properties, loading, error }
}

export function useFeaturedProperties() {
  const { properties, loading, error } = useProperties()

  // Prioriza propriedades featured, depois as n√£o-featured
  const featured = properties.filter(p => p.featured)
  const nonFeatured = properties.filter(p => !p.featured)

  // Combina: primeiro as featured, depois as n√£o-featured
  const allProperties = [...featured, ...nonFeatured]

  // Limita a 9 propriedades para a homepage
  const limitedProperties = allProperties.slice(0, 9)

  return { 
    featuredProperties: limitedProperties,
    loading, 
    error 
  }
}

export function useHomepageSettings() {
  const [settings, setSettings] = useState<HomepageSettings | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    async function loadSettings() {
      try {
        setLoading(true)
        const data = await getHomepageSettings()
        setSettings(data)
      } catch (err) {
        console.error('Error loading homepage settings:', err)
        setError('Failed to load homepage settings')
      } finally {
        setLoading(false)
      }
    }

    loadSettings()
  }, [])

  return { settings, loading, error }
}

export function useProperty(id: string | null) {
  const [property, setProperty] = useState<any>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    if (!id) {
      setLoading(false)
      return
    }

    async function loadProperty() {
      try {
        setLoading(true)
        const { getProperty } = await import('./admin-helpers')
        const data = await getProperty(id)
        if (data) {
          setProperty(convertToDisplayProperty(data))
        } else {
          setProperty(null)
        }
      } catch (err: any) {
        console.error('Error loading property:', err)
        setError(err.message || 'Failed to load property')
        setProperty(null)
      } finally {
        setLoading(false)
      }
    }

    loadProperty()
  }, [id])

  return { property, loading, error }
}

components/OptimizedMotion.tsx.content:
"use client"

import { motion, Variant, Transition, MotionStyle } from 'framer-motion'
import { ReactNode, useMemo, CSSProperties } from 'react'
import { useMobileOptimizedAnimation } from '../lib/useMobileOptimizedAnimation'

interface OptimizedMotionProps {
  children: ReactNode
  className?: string
  initial?: boolean | Variant | any
  whileInView?: Variant | any
  viewport?: {
    once?: boolean
    margin?: string
    amount?: number | 'some' | 'all'
  }
  transition?: Transition
  style?: MotionStyle
}

/**
 * Optimized motion component for mobile devices
 * Uses GPU-accelerated transforms and reduced animation complexity on mobile
 */
export function OptimizedMotion({ 
  children, 
  className = '',
  initial,
  whileInView,
  viewport,
  transition,
  style
}: OptimizedMotionProps) {
  const { isMobile, isIOS, getAnimationProps, gpuAccelerated } = useMobileOptimizedAnimation()
  const animProps = getAnimationProps()

  // Convert y/x animations to transform for better performance
  const optimizedInitial = useMemo(() => {
    if (!initial) return initial
    if (typeof initial === 'object' && 'y' in initial) {
      return { ...initial, y: initial.y, transform: `translateY(${initial.y}px)` }
    }
    return initial
  }, [initial])

  const optimizedWhileInView = useMemo(() => {
    if (!whileInView) return whileInView
    if (typeof whileInView === 'object' && 'y' in whileInView) {
      return { ...whileInView, y: whileInView.y }
    }
    return whileInView
  }, [whileInView])

  // Optimize transition for mobile
  const optimizedTransition = useMemo(() => {
    if (isMobile || isIOS) {
      return {
        ...animProps.transition,
        ...transition,
        // Force GPU acceleration
        type: 'tween' as const,
      } as any
    }
    return (transition || animProps.transition) as any
  }, [isMobile, isIOS, transition, animProps])

  // Optimize viewport for mobile
  const optimizedViewport = useMemo(() => {
    if (isMobile || isIOS) {
      return {
        once: true,
        margin: '0px', // Reduce margin to prevent flickering
        amount: 0.2, // Trigger earlier
        ...viewport,
      }
    }
    return {
      once: true,
      margin: '-50px',
      ...viewport,
    }
  }, [isMobile, isIOS, viewport])

  return (
    <motion.div
      initial={optimizedInitial}
      whileInView={optimizedWhileInView}
      viewport={optimizedViewport}
      transition={optimizedTransition}
      className={`${gpuAccelerated} ${className}`}
      style={{
        ...style,
        backfaceVisibility: 'hidden',
        perspective: 1000,
        WebkitBackfaceVisibility: 'hidden',
        WebkitPerspective: 1000,
      } as any}
    >
      {children}
    </motion.div>
  )
}

