    e: React.ChangeEvent<HTMLInputElement>,
    imageType: 'aboutUs' | 'ourApproach1' | 'ourApproach2' | 'heroPoster'
  ) => {
    const file = e.target.files?.[0]
    if (!file) return

    try {
      setUploading(imageType)

